import java.util.*;
public class EvenOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int n;
		Scanner sc= new Scanner(System.in);
		n = sc.nextInt();
		if(n%2==0)
		{
			System.out.println("Even");
			
		}
		
		else
		{
			System.out.println("Odd");
		}
		
	}

}
