import java.util.*;
public class Bitwise {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	Scanner sc = new Scanner (System.in);
	int a = sc.nextInt();
	int b= sc.nextInt();
	System.out.println(a|b);
	System.out.println(a&b);
	System.out.println(a^b);
	System.out.println(~a);
	System.out.println(a>>b);
	
		

	}

}
